import { Confirm } from "../../confirm.ts";
import { snapshotTest } from "jsr:@cliffy/testing@1.0.0-rc.8";

await snapshotTest({
  name: "confirm prompt",
  meta: import.meta,
  osSuffix: ["windows"],
  steps: {
    "should confirm": { stdin: ["y", "\n"] },
    "should not confirm": { stdin: ["n", "\n"] },
    "should not confirm by default": { stdin: ["", "\n"] },
  },
  async fn() {
    await Confirm.prompt({
      message: "Please confirm",
      hint: "some hint",
      default: false,
    });
  },
});
